mapper.insert:
DELETE FROM RESTAURANT_TIME WHERE POST_NO = #{postNo};
INSERT INTO RESTAURANT_TIME(POST_NO, RESTAURANT_DAY, RESTAURANT_OPEN, RESTAURANT_CLOSE, RESTAURANT_BREAK, RESTAURANT_LAST, RESTAURANT_DAY_OFF) VALUES
(#{postNo}, #{item.restaurantDay}, #{item.restaurantOpen}, #{item.restaurantClose}, #{item.restaurantBreak}, #{item.restaurantLastOrder}, #{item.restaurantDayOff})

DELETE FROM IMAGE WHERE POST_NO = #{postNo};
INSERT INTO IMAGE(POST_NO, IMAGE) VALUES
(#{posttNo}, #{item})

mapper.update:
UPDATE COMMUNITY
POST_TITLE = #{postTitle}, POST_TEXT = #{postText}, RESTAURANT_NAME = #{restaurantName}, RESTAURANT_TEL = #{restaurantTel}, STREET_ADDRESS = #{streetAddress}, AREA_ADDRESS = #{areaAddress}, MENU_TYPE = #{menuType}, MAIN_MENU_TITLE = #{mainMenuTitle}, MAIN_MENU_PRICE = #{mainMenuPrice}
WHERE POST_NO = #{postNo}

mapper.select:
SELECT *
FROM COMMUNITY
ORDER BY POST_NO LIMIT #{startRowNum}, #{pageSize}
