mapper.select:
		select review_no
        from review 
		WHERE member_id = #{member.memberId} AND reservation_no = #{reservationNo}

mapper.update:
UPDATE RESERVATION
FIXED_DATE = now(), RESERVATION_STATUS = #{reservationStatus}, REFUND_STATUS = #{refundStatus},
CANCEL_DATE = now(),
FIXED_STATUS = #{fixedStatus}, RESERVATION_CANCEL_TYPE = #{reservationCancelReason}, RESERVATION_CANCEL_DETAIL = #{reservationCancelDetail}
WHERE reservation_no = #{reservationNo}
