mapper.insert:
DELETE FROM HASHTAG_LOG WHERE review_no = #{reviewNo};
INSERT INTO HASHTAG_LOG(review_no, hashtag_no) VALUES
(#{reviewNo}, #{item})

DELETE FROM IMAGE WHERE review_no = #{reviewNo};
INSERT INTO IMAGE(review_no, image) VALUES
(#{reviewNo}, #{item})

mapper.update:
UPDATE REVIEW
review_detail = #{reviewDetail}, scope_taste = #{scopeTaste}, scope_kind = #{scopeKind}, scope_clean = #{scopeClean}, avg_scope = #{avgScope}, review_show_status = #{reviewShowStatus}
WHERE review_no = #{reviewNo}

mapper.delete:
DELETE FROM HASHTAG_LOG WHERE review_no = #{reviewNo};
DELETE FROM IMAGE WHERE review_no = #{reviewNo};
DELETE FROM MARK WHERE review_no = #{reviewNo};
DELETE FROM REPORT WHERE review_no = #{reviewNo};
DELETE FROM REVIEW WHERE review_no = #{reviewNo};

mapper.select:
SELECT * FROM (SELECT * FROM review AS r
WHERE r.restaurant_no = #{restaurantNo}
WHERE r.member_id LIKE #{id}
ORDER BY r.review_no limit 0,10) AS r INNER JOIN (SELECT image, review_no FROM image) AS i ON (r.review_no = i.review_no) INNER JOIN (SELECT hl.HASHTAG_no, h.hashtag, review_no FROM HASHTAG_LOG as hl, HASHTAG as h WHERE hl.hashtag_no = h.hashtag_no) as hh on(r.review_no = hh.review_no)

SELECT * FROM (SELECT * FROM review AS r
WHERE r.member_id LIKE #{id}
ORDER BY r.review_no limit 0,10) AS r Inner Join (select review_no From mark) AS m ON(r.review_no = m.review_no) INNER JOIN (SELECT image, review_no FROM image) AS i ON (r.review_no = i.review_no) INNER JOIN (SELECT hl.HASHTAG_no, h.hashtag, review_no FROM HASHTAG_LOG as hl, HASHTAG as h WHERE hl.hashtag_no = h.hashtag_no) as hh on(r.review_no = hh.review_no)

SELECT count(review_no)
FROM MARK
WHERE review_no = #{value};
