mapper.insert:
DELETE FROM HASHTAG_LOG WHERE review_no = #{reviewNo};
INSERT INTO HASHTAG_LOG(review_no, hashtag_no) VALUES
(#{reviewNo}, #{item.hashTagNo})

DELETE FROM IMAGE WHERE review_no = #{reviewNo};
INSERT INTO IMAGE(review_no, image) VALUES
(#{reviewNo}, #{item})

mapper.update:
UPDATE REVIEW
review_detail = #{reviewDetail}, scope_taste = #{scopeTaste}, scope_kind = #{scopeKind}, scope_clean = #{scopeClean}, avg_scope = #{avgScope},
review_show_status = #{reviewShowStatus}
WHERE review_no = #{reviewNo}

mapper.delete:
DELETE FROM HASHTAG_LOG WHERE review_no = #{reviewNo};
DELETE FROM IMAGE WHERE review_no = #{reviewNo};
DELETE FROM MARK WHERE review_no = #{reviewNo};
DELETE FROM REPORT WHERE review_no = #{reviewNo};
DELETE FROM ALARM WHERE review_no = #{reviewNo};
DELETE FROM REVIEW WHERE review_no = #{reviewNo};

mapper.select:
SELECT * FROM (SELECT r.review_no, r.member_id, r.reservation_no, r.restaurant_no, r.review_detail, r.review_reg_date, r.review_show_status, r.scope_taste, r.scope_kind, r.scope_clean, r.avg_scope, rep.report_count FROM review AS r LEFT Outer join(select review_no, COUNT(review_no) AS report_count from report GROUP BY review_no) as rep on (rep.review_no = r.review_no)
WHERE r.restaurant_no = #{restaurantNo}
AND review_show_status = 1
WHERE r.member_id = #{member.memberId}
ORDER BY r.review_no DESC limit #{search.startRowNum}, #{search.pageSize})AS re LEFT OUTER JOIN (SELECT image, review_no FROM image) AS i ON (re.review_no = i.review_no) LEFT OUTER JOIN (SELECT hl.HASHTAG_no, h.hashtag, review_no FROM HASHTAG_LOG as hl, HASHTAG as h WHERE hl.hashtag_no = h.hashtag_no) AS hh ON(re.review_no = hh.review_no) INNER JOIN (SELECT member_id, nickname, profile_image FROM Member) AS m ON (re.member_id = m.member_id);

SELECT count(review_no)
FROM MARK
WHERE review_no = #{value};

SELECT count(*)
FROM REVIEW


  		WHERE restaurant_no = #{restaurantNo}




 				AND review_show_status = 1





  			WHERE member_id = #{member.memberId}

SELECT ifnull(truncate(avg(avg_scope),1),0)
FROM review
WHERE restaurant_no = #{value} AND review_show_status = 1;
